import requestsfrom bs4 import BeautifulSoupclass Crawler(object):    @staticmethod    def crawl(url):        source_code = requests.get(url)        plain_text = source_code.text        soup = BeautifulSoup(plain_text, "lxml")        links_div = soup.find(id="ires")        # links_url = links_div.soup.find_all("h3 > a")        # print(links_div)        for link in soup.select("h3 > a"):            print(link)            prop = link.get("class")            if prop != None and prop[0] == "g":                print("%s : %s" % (link.get("href"), prop[0]))            #     # print(link.get("href"))