import codecsclass Viewer(object):    def __init__(self):        self.file = codecs.open( "parsing_data.txt", "r+", "utf-16")    def read(self):        lines = self.file.readlines()        data = []        for line in lines:            l = line.split('\t')            # id, 제목, 저자, 출판사, 출판년도, isbn, isbn - 제거, 찾을거            d = [l[2], l[3], l[4], l[5], l[6], l[10], l[11], l[12]]            data.append(d)        return data    def write(self, value, idx):        l = self.file[idx].split('\t')        l[12] = value        str = '\t'.join(l) + '\n'        self.file[idx] = str        print(idx + " 번째 write value : " + value)