import codecsclass Viewer(object):    def __init__(self):        self.input_file = codecs.open("parsing_data.txt", "r", "utf-16")        self.output_file = codecs.open("output_data(3).txt", "a", "utf-16")        self.lines = self.input_file.readlines()    def read(self):        data = []        for line in self.lines:            l = line.split('\t')            # id, 제목, 저자, 출판사, 출판년도, isbn, isbn - 제거, 찾을거            d = [l[2], l[3], l[4], l[5], l[6], l[10], l[11], l[12]]            data.append(d)        return data    def write(self, value, idx):        l = self.lines[idx].split('\t')        l[12] = value        add = '\t'.join(l)        self.output_file.write(add)        print(str(idx) + " : " + add)