import codecsclass Viewer(object):    @staticmethod    def read():        f = codecs.open( "parsing_data.txt", "r", "utf-16" )        lines = f.readlines()        data = []        for line in lines:            l = line.split('\t')            # id, 제목, 저자, 출판사, 출판년도, isbn, isbn - 제거, 찾을거            d = [l[2], l[3], l[4], l[5], l[6], l[10], l[11], l[12]]            data.append(d)        f.close()        return data